import React, {useState, useEffect} from 'react';
import axios from 'axios';

import classes from './StudentsView.module.css';

const StudentsView = props => {
    const [studentList, updateList] = useState([]);
    const [nationalities, updateNationalities] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:8080/students')
            .then(res => {
                updateList(res.data);
                let nationalities = res.data.map(item => item.nationality);
                updateNationalities(nationalities);
            })
            .catch(err => console.log(err));
    })
    let content = null;
    if (!studentList.length){
        content = <Spinner />
    } else {
        content = (
            <div>
                <select value={nationalities[0]}>
                    {nationalities.map(item => <option value={item}>{item.toUpperCase()}</option>)}
                </select>
            </div>
        )
    }
    return (
        <div className={classes.StudentsView}>
            {content}
        </div>
    )
}

export default StudentsView;

const Spinner = () => (
    <div className={classes.Spinner}></div>
)
