import React, {useState, useEffect} from 'react';
import axios from 'axios';

import classes from './StudentsView.module.css';

const StudentsView = props => {
    const [studentList, updateList] = useState([]);
    const [nationalities, updateNationalities] = useState([]);
    const [selectedNationality, updateSelectedNationality] = useState('');
    useEffect(() => {
        axios.get('http://localhost:8080/students')
            .then(res => {
                updateList(res.data);
                let nationalities = Array.from(new Set(res.data.map(item => item.nationality)));
                updateNationalities(nationalities);
            })
            .catch(err => console.log(err));
    });

    const selectionChangedHandler = event => {
        updateSelectedNationality(event.target.value);
        console.log(selectedNationality, event.target.value)
    }
    let content = null;
    if (!studentList.length){
        content = <Spinner />
    } else {
        content = (
            <div>
                <select onChange={selectionChangedHandler} >
                    {nationalities.map((item, i) => <option key={item+i} value={item}>{item.toUpperCase()}</option>)}
                </select>
            </div>
        )
    }
    return (
        <div className={classes.StudentsView}>
            {content}
        </div>
    )
}

export default StudentsView;

const Spinner = () => (
    <div className={classes.Spinner}></div>
)
